import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = () => {
  const [movieLists, setMovieLists] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    fetchMovieLists();
  }, []);

  const fetchMovieLists = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/movielists');
      setMovieLists(response.data);
    } catch (error) {
      setErrorMessage('Error fetching movie lists. Please try again later.');
    }
  };

  const renderMovieLists = () => {
    return (
      <div>
        {movieLists.map((list) => (
          <div key={list._id}>
            <h2>{list.name}</h2>
            <ul>
              {list.movies.map((movie, index) => (
                <li key={index}>{movie}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>My Movie Lists</h1>
      {errorMessage && <p>{errorMessage}</p>}
      {renderMovieLists()}
    </div>
  );
};

export default Home;
