import React, { useState } from 'react';
import axios from 'axios';

function MovieSearch() {
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.get(`http://localhost:5000/api/omdb/search?query=${query}`);
      setMovies(res.data);
    } catch (error) {
      console.error('Error searching for movies', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch} className="flex mb-4">
        <input type="text" value={query} onChange={(e) => setQuery(e.target.value)} className="w-full p-2 border rounded" placeholder="Search for a movie..." />
        <button type="submit" className="ml-2 p-2 bg-blue-500 text-white rounded">Search</button>
      </form>
      <div>
        {movies.map(movie => (
          <div key={movie.imdbID} className="mb-4 p-4 border rounded">
            <h3 className="text-xl">{movie.Title}</h3>
            <p>{movie.Year}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MovieSearch;
