import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getLists, deleteMovieFromList } from '../services/api';
import axios from 'axios';

const ListDetails = ({}) => {
    const { id } = useParams();
    const [list, setList] = useState(null);
    const [movies, setMovies] = useState(list.movies);

    useEffect(() => {
        const fetchLists = async () => {
            const data = await getLists();
            const selectedList = data.find((l) => l._id === id);
            setList(selectedList);
        };
        fetchLists();
    }, [id]);

    const handleDeleteMovie =  async (movieId) => {
        try {
          const token = localStorage.getItem('jwtToken'); // Ensure you're using the correct key
    
          await axios.delete(`http://localhost:5000/api/lists/${list._id}/movies/${movieId}`, {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });
    
          // Update the state after successfully deleting the movie
          setMovies(prevMovies => prevMovies.filter(movie => movie !== movieId));
        } catch (error) {
          console.error('Error deleting movie', error);
        }
      };

    if (!list) return <div>Loading...</div>;

    return (
        <div className="p-6">
            <h2 className="text-2xl mb-4">{list.name}</h2>
            <ul>
                {list.movies.map((movie) => (
                    <li key={movie.imdbID} className="mb-4 flex justify-between items-center">
                        <span>{movie.Title} ({movie.Year})</span>
                        <button
                            className="bg-red-500 text-white p-2 rounded"
                            onClick={() => handleDeleteMovie(movie.imdbID)}
                        >
                            Remove
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ListDetails;
