// Home.js
import React, { useState, useEffect } from 'react';
import { searchMovies, getLists, createList, addMovieToList } from '../services/api';
import { Link } from 'react-router-dom';
import SearchComponent from '../components/SearchComponent';

const Home = () => {
    const [movies, setMovies] = useState([]);
    const [lists, setLists] = useState([]);
    const [newListName, setNewListName] = useState('');
    const [selectedListId, setSelectedListId] = useState('');
    const [showListDropdown, setShowListDropdown] = useState(false);
    const [isPublicList, setIsPublicList] = useState(true); // State to track list visibility

    useEffect(() => {
        const fetchLists = async () => {
            const data = await getLists();
            setLists(data);
        };
        fetchLists();
    }, []);

    const handleSearch = async (searchTerm) => {
        try {
            const data = await searchMovies(searchTerm);
            setMovies(data.Search);
            setShowListDropdown(true); // Show the list dropdown after search results are obtained
        } catch (error) {
            console.error('Failed to fetch search results:', error);
            // Handle the error, e.g., display an error message to the user
        }
    };

    const handleAddMovie = async (movie) => {
        if (!selectedListId) {
            window.alert('Please select a list to add the movie to.');
            return;
        }

        try {
            await addMovieToList(selectedListId, movie);
            window.alert('Movie added to list successfully');
        } catch (error) {
            console.error('Failed to add movie to list:', error);
            window.alert('Failed to add movie to list');
        }
    };

    const handleCreateList = async (e) => {
        e.preventDefault();
        try {
            const data = await createList({ name: newListName, isPublic: isPublicList });
            setLists([...lists, data]);
            setNewListName('');
        } catch (error) {
            console.error('Failed to create list:', error);
        }
    };
    return (
        <div className="p-6">
            
            <SearchComponent handleSearch={handleSearch} />
            {/* Remaining content */}
        


            <div>
                <h2 className="text-2xl mb-4">Your Lists</h2>
                <div className="mb-6">
                    <form onSubmit={handleCreateList} className="flex items-center">
                        <input
                            type="text"
                            placeholder="New List Name"
                            value={newListName}
                            onChange={(e) => setNewListName(e.target.value)}
                            className="p-2 border mr-2"
                        />
                        <select
                            value={isPublicList}
                            onChange={(e) => setIsPublicList(e.target.value === 'true')}
                            className="p-2 border mr-2"
                        >
                            <option value="true">Public</option>
                            <option value="false">Private</option>
                        </select>
                        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
                            Create List
                        </button>
                    </form>
                </div>
                <ul>
                    {lists.map((list) => (
                        <li key={list._id} className="mb-2">
                            <Link to={`/list/${list._id}`} className="text-blue-500">
                                {list.name} ({list.isPublic ? 'Public' : 'Private'})
                            </Link>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
