import React, { useState, useEffect } from 'react';
import { searchMovies, getLists, createList } from '../services/api';
import { Link } from 'react-router-dom';
import SearchComponent from '../components/SearchComponent';

const Home = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [movies, setMovies] = useState([]);
    const [lists, setLists] = useState([]);
    const [newListName, setNewListName] = useState('');
    const [isPublicList, setIsPublicList] = useState(true); // State to track list visibility

    useEffect(() => {
        const fetchLists = async () => {
            const data = await getLists();
            setLists(data);
        };
        fetchLists();
    }, []);

    const handleSearch = async (searchTerm) => {
        try {
            const data = await searchMovies(searchTerm);
            setMovies(data.Search);
        } catch (error) {
            console.error('Failed to fetch search results:', error);
            // Handle the error, e.g., display an error message to the user
        }
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        handleSearch(searchTerm);
        setShowListDropdown(true); // Show the list dropdown after search results are obtained
    };

    const handleCreateList = async (e) => {
        e.preventDefault();
        try {
            const data = await createList({ name: newListName, isPublic: isPublicList });
            setLists([...lists, data]);
            setNewListName('');
        } catch (error) {
            console.error('Failed to create list:', error);
        }
    };

    return (
        <div className="p-6">
            <div className="mb-6">
            <form onSubmit={handleSubmit} className="flex items-center justify-center">
                <input
                    type="text"
                    placeholder="Search for Movies or TV shows"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="bg-white-200 w-1/3 text-black-300 font-mono ring-1 ring-black-10 focus:ring-2 focus:ring-blue-400 outline-none duration-300 placeholder:text-zinc-800 placeholder:opacity-80 rounded-full px-4 py-1 shadow-md focus:shadow-lg focus:shadow-blue-200"
                />
                <button
                    type="submit"
                    className="ml-2 relative py-2 px-5 text-black text-base font-bold rounded-full overflow-hidden bg-zinc-200 rounded-full transition-all duration-400 ease-in-out shadow-md hover:scale-105 hover:text-white hover:shadow-lg active:scale-90 before:absolute before:top-0 before:-left-full before:w-full before:h-full before:bg-gradient-to-r before:from-blue-500 before:to-blue-300 before:transition-all before:duration-500 before:ease-in-out before:z-[-1] before:rounded-full hover:before:left-0"
                >
                    Search
                </button>
            </form>
            </div>

            <div>
                <h2 className="text-2xl mb-4">Your Lists</h2>
                <div className="mb-6">
                    <form onSubmit={handleCreateList} className="flex items-center">
                        <input
                            type="text"
                            placeholder="New List Name"
                            value={newListName}
                            onChange={(e) => setNewListName(e.target.value)}
                            className="p-2 border mr-2"
                        />
                        <select
                            value={isPublicList}
                            onChange={(e) => setIsPublicList(e.target.value === 'true')}
                            className="p-2 border mr-2"
                        >
                            <option value="true">Public</option>
                            <option value="false">Private</option>
                        </select>
                        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
                            Create List
                        </button>
                    </form>
                </div>
                <ul>
                    {lists.map((list) => (
                        <li key={list._id} className="mb-2">
                            <Link to={`/list/${list._id}`} className="text-blue-500">
                                {list.name} ({list.isPublic ? 'Public' : 'Private'})
                            </Link>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
