// Home.js
import React, { useState, useEffect } from 'react';
import { searchMovies, getLists, createList, addMovieToList } from '../services/api';
import { Link } from 'react-router-dom';

const Home = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [movies, setMovies] = useState([]);
    const [lists, setLists] = useState([]);
    const [newListName, setNewListName] = useState('');
    const [selectedListId, setSelectedListId] = useState('');
    const [showListDropdown, setShowListDropdown] = useState(false);
    const [isPublicList, setIsPublicList] = useState(true); // State to track list visibility

    useEffect(() => {
        const fetchLists = async () => {
            const data = await getLists();
            setLists(data);
        };
        fetchLists();
    }, []);

    const handleSearch = async (e) => {
        e.preventDefault();
        // Check if the search term is empty
        if (!searchTerm.trim()) {
            // Show an alert to the user
            window.alert('Please enter a search term.');
            return;
        }
    
        try {
            const data = await searchMovies(searchTerm);
            setMovies(data.Search);
            setShowListDropdown(true); // Show the list dropdown after search results are obtained
        } catch (error) {
            console.error('Failed to fetch search results:', error);
            // Handle the error, e.g., display an error message to the user
        }
    };
    

    const handleCreateList = async (e) => {
        e.preventDefault();
        try {
            const data = await createList({ name: newListName, isPublic: isPublicList });
            setLists([...lists, data]);
            setNewListName('');
        } catch (error) {
            console.error('Failed to create list:', error);
        }
    };

    const handleAddMovie = async (movie) => {
        if (!selectedListId) {
            window.alert('Please select a list to add the movie to.');
            return;
        }

        try {
            await addMovieToList(selectedListId, movie);
            window.alert('Movie added to list successfully');
        } catch (error) {
            console.error('Failed to add movie to list:', error);
            window.alert('Failed to add movie to list');
        }
    };

    return (
        <div className="p-6">
            <div className="mb-6 ">
                <form onSubmit={handleSearch} className="flex items-center justify-center">
                    <input
                        type="text"
                        placeholder="Search Movies"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="bg-white-200 w-1/2 text-black-300 font-mono ring-1 ring-black-10 focus:ring-2 focus:ring-blue-400 outline-none duration-300 placeholder:text-zinc-800 placeholder:opacity-80 rounded-full px-4 py-1 shadow-md focus:shadow-lg focus:shadow-blue-200"
                    />
                    <button type="submit" className="ml-2 relative py-2 px-8 text-black text-base font-bold nded-full overflow-hidden bg-zinc-200 rounded-full transition-all duration-400 ease-in-out shadow-md hover:scale-105 hover:text-white hover:shadow-lg active:scale-90 before:absolute before:top-0 before:-left-full before:w-full before:h-full before:bg-gradient-to-r before:from-blue-500 before:to-blue-300 before:transition-all before:duration-500 before:ease-in-out before:z-[-1] before:rounded-full hover:before:left-0">
                        Search
                    </button>
                    <Link to={"/list/:id"}>
                    <button type="submit" className="ml-2 relative py-2 px-8 text-black text-base font-bold nded-full overflow-hidden bg-zinc-200 rounded-full transition-all duration-400 ease-in-out shadow-md hover:scale-105 hover:text-white hover:shadow-lg active:scale-90 before:absolute before:top-0 before:-left-full before:w-full before:h-full before:bg-gradient-to-r before:from-blue-500 before:to-blue-300 before:transition-all before:duration-500 before:ease-in-out before:z-[-1] before:rounded-full hover:before:left-0">
                        Search
                    </button>
                    </Link>
                </form>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                {movies.map((movie) => (
                    <div key={movie.imdbID} className="border p-4 rounded">
                        <h3 className="text-xl mb-2">{movie.Title}</h3>
                        <img src={movie.Poster} alt={movie.Title} className="mb-2" />
                        <div className="flex justify-between items-center">
                            <span>Year: {movie.Year}</span>
                            {showListDropdown && (
                                <div>
                                    <select
                                        value={selectedListId}
                                        onChange={(e) => setSelectedListId(e.target.value)}
                                    >
                                        <option value="">Select List</option>
                                        {lists.map((list) => (
                                            <option key={list._id} value={list._id}>
                                                {list.name} ({list.isPublic ? 'Public' : 'Private'})
                                            </option>
                                        ))}
                                    </select>
                                    <button
                                        className="bg-green-500 text-white p-2 rounded ml-2"
                                        onClick={() => handleAddMovie(movie)}
                                    >
                                        Add to List
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                ))}
            </div>

            <div>
                <h2 className="text-2xl mb-4">Your Lists</h2>
                <div className="mb-6">
                    <form onSubmit={handleCreateList} className="flex items-center">
                        <input
                            type="text"
                            placeholder="New List Name"
                            value={newListName}
                            onChange={(e) => setNewListName(e.target.value)}
                            className="p-2 border mr-2"
                        />
                        <select
                            value={isPublicList}
                            onChange={(e) => setIsPublicList(e.target.value === 'true')}
                            className="p-2 border mr-2"
                        >
                            <option value="true">Public</option>
                            <option value="false">Private</option>
                        </select>
                        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
                            Create List
                        </button>
                    </form>
                </div>
                <ul>
                    {lists.map((list) => (
                        <li key={list._id} className="mb-2">
                            <Link to={`/list/${list._id}`} className="text-blue-500">
                                {list.name} ({list.isPublic ? 'Public' : 'Private'})
                            </Link>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
