import React, { useState } from 'react';
import axios from 'axios';

function MovieList({ list }) {
  const [movies, setMovies] = useState(list.movies);

  const handleDelete = async (movieId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.delete(`http://localhost:5000/api/movielists/${list._id}/${movieId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMovies(res.data.movies);
    } catch (error) {
      console.error('Error deleting movie', error);
    }
  };

  return (
    <div className="mb-10">
      <h2 className="text-2xl mb-4">{list.name}</h2>
      <ul>
        {movies.map(movie => (
          <li key={movie} className="mb-2 flex justify-between items-center">
            <span>{movie}</span>
            <button onClick={() => handleDelete(movie)} className="bg-red-500 text-white p-2 rounded">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MovieList;
